typedef vector<unsigned int> Array;
typedef vector<Array> Matrix;

Matrix mul(const Matrix &a,const Matrix &b){
    Matrix res(a.size(),Array(b[0].size()));
    REP(i,a.size())
        REP(j,b[0].size())
            REP(k,a[0].size())
                res[i][j] ^= a[i][k] & b[k][j];
    return res;
}

Matrix pow(const Matrix &a,int b){
    if(b==0){
        Matrix res(a.size(),Array(a.size()));
        REP(i,a.size()) res[i][i]=-1;
        return res;
    }
    Matrix res=pow(mul(a,a),b/2);
    if(b&1)res=mul(res,a);
    return res;
}

int rankk(Matrix M){
    int N = M.size();
    int W = M[0].size();
    int ret = 0;
    int j = 0;
    for(int i=0;i<N;i++){
        int ma=i;
        for(int k=i;k<N;k++) if(M[ma] < M[k]) ma = k;
        swap(M[i], M[ma]);
        for(;!M[i][j]&&j<W;j++);
        if(W<=j) return ret;
        for(int k=i+1;k<N;k++)
            if(M[k][j])
                for(int l=j;l<W;l++) M[k][l] ^= M[i][l];
        ret ++;
    }
    return ret;
}

template<int N, int MOD>
struct Comb{
    ll fact[N+1], inv[N+1];
    Comb(){
        fact[0] = inv[0] = inv[1] = 1;
        for(int i=2;i<=N;i++) inv[i] = (MOD+((ll)-inv[MOD%i]*(MOD/i))%MOD)%MOD;
        REPS(i, N){
            fact[i] = fact[i-1]*i%MOD;
            inv[i] = inv[i]*inv[i-1]%MOD;
        }
    }
    ll operator()(int n, int r){
        if(n < 0 || r < 0 || r > n) return 0;
        ll res = inv[r]*inv[n-r];
        if(res >= MOD) res%= MOD;
        res *= fact[n];
        if(res >= MOD) res%= MOD;
        return res;
    }
};
Comb<300000, MOD> C;

ll modpow(ll x, ll y){
    ll r = 1, a = x % MOD;
    while(y > 0){
        if((y&1) == 1) r = (r*a) % MOD;
        a = (a*a) % MOD;
        y /= 2;
    }
    return r;
}

