}


ll Rank(ll N, ll M, vector<vector<ll>> A){
  ll next = 0;
  rep(i, M){
    for(ll j = next; j < N; j++){
      if(A[j][i]){
        swap(A[next], A[j]);
      }
    }
    if(!A[next][i]) continue;
    rep(j, N)if(j != next){
      if(A[j][i]){
        rep(k, M) A[j][k] ^= A[next][k];
      }
    }
    next++;
    if(next == N + 1) break;
  }
  return next;
}

