const int mod = 998244353;

li pw(li x, li y) {
    return y ? ((y & 1) ? pw(x, y - 1) * x % mod : pw(x * x % mod, y / 2)) : 1;
}

int gauss (vector < vector<int> > a) {
    int n = (int) a.size();
    int m = (int) a[0].size();
    int rank = 0;

    for (int col=0, row=0; col<m && row<n; ++col) {
        int sel = row;
        for (int i=row; i<n; ++i)
            if (a[i][col]) {
                sel = i;
                break;
            }
        if (!a[sel][col])
            continue;
        ++rank;
        for (int i=col; i < m; ++i)
            swap (a[sel][i], a[row][i]);

        for (int i=0; i<n; ++i)
            if (i != row && a[i][col]) {
                for (int j = col; j < m; ++j)
                    a[i][j] ^= a[row][j];
            }
        ++row;
    }
    return rank;
}

void solve(bool __attribute__((unused)) read) {
    int n, m;
	cin >> n >> m;
