    for (int i = 0; i < n; ++i) {
        a[i].resize(m);
        for (int j = 0; j < m; ++j) {
            cin >> a[i][j];
        }
    }
    int rank = gauss(a);
    // cout << rank << endl;
    // cout << pw(2, n) << endl;
    // cout << pw(2, n - rank) << endl;
    li ans = (pw(2, n) - pw(2, n - rank) + mod) % mod * pw(2, m - 1) % mod;
    ans = (ans % mod + mod) % mod;
    cout << ans << "\n";
}

