ll beki(ll a, ll b) {
    if(b == 0) return 1;
    ll ans = beki(a, b / 2);
    ans = ans * ans % mod;
    if(b % 2 == 1) {
        ans = ans * a % mod;
    }
    return ans;
}

ll Rank(ll index) {
    if(index > min(N, M)) return 0;
    int nonzero_i = -1;
    int nonzero_j = -1;
    for(int i = index; i <= N; i++) {
        for(int j = index; j <= M; j++) {
            if(a[i][j] == 1) {
                nonzero_i = i;
                nonzero_j = j;
            }
        }
    }
    if(nonzero_i == -1) return 0;
    for(int i = index; i <= N; i++) {
        swap(a[i][nonzero_j], a[i][index]);
    }
    for(int j = index; j <= M; j++) {
        swap(a[nonzero_i][j], a[index][j]);
    }
    for(int i = index + 1; i <= N; i++) {
        if(a[i][index] == 0) continue;
        for(int j = index; j <= M; j++) {
            a[i][j] = (a[i][j] + a[index][j]) % 2;
        }
    }
    for(int j = index + 1; j <= M; j++) {
        a[index][j] = 0;
    }
    return Rank(index + 1) + 1;
}

