template <typename T> LLI power(T n, T p, T m){if(p==0) return 1LL; if(p==1) return n; LLI k = power(n, p/2, m); return ((k*k)%m*(p%2?n:1))%m;}

const LLI mod = 998244353;
const double eps = 1e-9;

template <typename T> int rank_matrix(vector<vector<T>> mat){

  int n = mat.size();
  int m = mat[0].size();
  int p = 0;

  REP(i,n){
    if(p>=m) break;    

    bool b = true;
    FOR(j,i,n) if(abs(mat[j][p]) > eps) b = false;

    if(b){
      ++p;
      continue;
    }

    if(abs(mat[i][p]) <= eps ){
      FOR(j,i+1,n){
	if(abs(mat[j][p]) > eps){
	  swap(mat[i], mat[j]);
	  break;
	}
      }
    }

    FOR(j,i+1,n){
      double t = (double)mat[j][p] / mat[i][p];
      REP(k,m) mat[j][k] -= mat[i][k] * t;
    }
  }

  int r = 0;
  REP(i,n){
    REP(j,m){
      if(mat[i][j] != 0){
	++r;
	break;
      }
    }
  }

  return r;
}

int main(){
  cin.tie(0);
  ios::sync_with_stdio(false);

  int N,M;
  while(cin >> N >> M){
    vector<vector<double>> a(N, vector<double>(M)); cin >> a;
    int r = rank_matrix(a);
    dump(r);

    LLI ans = (power(2LL,(LLI)(M-1),mod) * ((power(2LL,(LLI)N,mod) - power(2LL,(LLI)(N-r),mod) + mod*mod) % mod)) % mod;
    cout << ans << endl;
  }

  return 0;
}

