long long fsp(int p) {
    long long rt = 1, base = 2;
    while (p) {
        if (p & 1) (rt *= base) %= mod;
        (base *= base) %= mod;
        p >>= 1;
    }
    return rt;
}

void work() {
    for (int i = m; i >= 1; --i) {
        if (!a[i]) continue;
        if (!base[i][i]) {
            sum--;
            for (int j = 1; j <= i; ++j) {
