                if (!base[j][i]) continue;
                for (int k = 1; k <= i; ++k) {
                    base[j][k] ^= base[i][k];
                }
            }
        } else {
            for (int j = 1; j <= i; ++j) {
                a[j] ^= base[i][j];
            }
        }
    }
