int calc_rank(vector<vector<int>> v) {
    int n = v.size(), m = v[0].size();
    int st = 0;
    rep(j,m) {
        int pv = -1;
        for (int i = st; i < n; i++) {
            if (v[i][j]) {
                pv = i;
                break;
