int gauss(vector<vector<int>> A) {
    int n = A.size(), m = A[0].size();
    int r = 0;
    for (int i = 0; r < n && i < m; ++i) {
        int piv = r;
        for (int j = r + 1; j < n; ++j)
            if (abs(A[j][i]) > abs(A[piv][i])) piv = j;
        A[r].swap(A[piv]);
        if (abs(A[r][i]) == 0) continue;
        for (int k = m - 1; k >= i; --k)
            A[r][k] /= A[r][i];
        for (int j = r + 1; j < n; ++j)
            for (int k = m - 1; k >= i; --k)
                A[j][k] ^= A[r][k] * A[j][i];
        ++r;
    }
    return r;
}
