void solve(long long N, long long M, std::vector<std::vector<long long>> a){
    For(i,N)For(j,M){
        mat[i+1][j+1]=(mat[i+1][j]+a[i][j])%2;
    }
    For(j,M)For(i,N){
        mat[i+1][j+1]+=mat[i][j+1];
        mat[i+1][j+1]%=2;
    }
    For(i,N)For(j,M){
        int is_even=mat[i+1][j]%2==0?1:0;
        cntj[0][i+1][j+1]=cntj[0][i+1][j]+is_even;
        cntj[1][i+1][j+1]=cntj[1][i+1][j]+1-is_even;
    }
    For(j,M)For(i,N){
        int is_even=mat[i][j+1]%2==0?1:0;
        cnti[0][i+1][j+1]=cnti[0][i][j+1]+is_even;
        cnti[1][i+1][j+1]=cnti[1][i][j+1]+1-is_even;
    }
    ll ans=0;
    For(i,N)For(j,M){
        if(mat[i+1][j+1]%2==0){
            ans+=cnti[0][i+1][j+1]*cntj[1][i+1][j+1];
            ans+=cnti[1][i+1][j+1]*cntj[0][i+1][j+1];
        }else{
            ans+=cnti[0][i+1][j+1]*cntj[0][i+1][j+1];
            ans+=cnti[1][i+1][j+1]*cntj[1][i+1][j+1];
        }
    }
    cout<<ans<<endl;
}

