        }
    int r = calc_rank(A);
    long long fact = modpow(2LL, N-r, MOD) * modpow(2LL, M-r, MOD) % MOD;
    long long res = 0;
    for (int i = 1; i <= r; ++i) {
        long long tmp = COM(r, i) * modpow(2LL, i-1, MOD) % MOD * modpow(2LL, r-i, MOD) % MOD;
        res = (res + tmp) % MOD;
    }
    res = (res * fact) % MOD;
    cout << res << endl;
}

