constexpr int mod = 998244353;
int R,C;
int A[312][312];
LL pow_mod(LL a,LL n,int M=mod){
    LL res=1;
    a%=M;
    while(n){
        if(n&1)
            res=(res*a)%M;
        a=(a*a)%M;
        n>>=1;
    }
    return res;
}
int get_rank(){
    int ret = 0;
    for(int c:range(C)){
        int id=-1;
        for(int i:range(ret,R)){
            if(A[i][c]==1){
                id=i;
            }
        }
        if(id==-1)continue;
        for(int j:range(C)){
            swap(A[ret][j],A[id][j]);
        }
        ret++;
        for(int i:range(ret,R)){
            if(A[i][c]==1)for(int j:range(C)){
                A[i][j] = A[i][j] xor A[ret-1][j];
            }
        }
    }
    return ret;
}
