#define set(b, i) (b[(i) >> 5] |= 1 << ((i) & 31))
#define val(b, i) (b[(i) >> 5] >> ((i) & 31) & 1)

int main() {
	scanf("%d%d", &N, &M);
	if (N > M) {
		for (int i = 0; i < N; ++i)
			for (int j = 0; j < M; ++j)
				scanf("%d", &A[i][j]);
	}
	else {
		for (int i = 0; i < N; ++i)
			for (int j = 0; j < M; ++j)
				scanf("%d", &A[j][i]);
		std::swap(N, M);
	}
	Sz = (M + 31) >> 5;
	for (int j = 0; j < M; ++j) B[j] = new ui[Sz];
	tmp = new ui[Sz];
	for (int i = 0; i < N; ++i) {
		for (int j = 0; j < M; ++j) if (A[i][j]) set(tmp, j);
		for (int j = M - 1; ~j; --j) if (val(tmp, j)) {
			if (val(B[j], j)) for (int k = 0; k < Sz; ++k) tmp[k] ^= B[j][k];
			else {
				for (int k = 0; k < Sz; ++k) B[j][k] = tmp[k];
