class Solver2 {
 public:
  int n, m;
  vector<vector<int>> a;
  Solver2(LL n, LL m) : n(n), m(m), a(n, vector<int>(m)){};

  int sweep(vector<vector<int>> X) {
    int rank = 0;
    for (int col = 0; col < m; ++col) {
      int nonzeroRow = -1;
      for (int r = rank; r < n; ++r) {
        if (X[r][col]) {
          nonzeroRow = r;
          break;
        }
      }
      if (nonzeroRow == -1) continue;
      if (nonzeroRow > rank) swap(a[rank], a[nonzeroRow]);
      for (int r = rank + 1; r < n; ++r) {
        if (X[r][col]) {
          for (int c = col; c < m; ++c) {
            X[r][c] ^= X[rank][c];
          }
        }
      }
      rank++;
    }
    return min({n,m,rank});
  }
  void solve() {
    for (int i = 0; i < n; ++i) {
      for (int j = 0; j < m; ++j) {
        cin >> a[i][j];
      }
    }
    int rank = sweep(a);
    if (rank == 0)
      cout << 0 << endl;
    else
      cout << (Modint<>(2).mpow(rank) - 1) * Modint<>(2).mpow(n - rank) * Modint<>(2).mpow(m - 1) << endl;
  }
};
