const int ms = 303;
const int MOD = 998244353;

std::bitset<ms> mat[ms];

struct Gauss {
	Gauss() {
		rank = 0;
		for(int i = 0; i < ms; i++) {
			eq[i].reset();
		}
	}
	int rank;
	std::bitset<ms> eq[ms];

	int add(std::bitset<ms> a) {
		for(int i = 0; i < ms; i++) {
			if(!eq[i][i] && !a[i]) continue;
			if(eq[i][i]) {
				if(a[i]) {
					a ^= eq[i];
				}
			} else {
				eq[i] = a;
				rank++;
				return i;
			}
		}
		return -1;
	}

	std::bitset<ms> reduce(std::bitset<ms> a) {
		for(int i = 0; i < ms; i++) {
			if(eq[i][i] && a[i]) {
				a ^= eq[i];
			}
		}
		return a;
	}
};

long long pot[ms + ms];

int main() {
	pot[0] = 1;
	for(int i = 1; i < ms + ms; i++) {
		pot[i] = pot[i-1] * 2 % MOD;
	}
	int n, m;
	std::cin >> n >> m;
	for(int i = 0; i < n; i++) {
		for(int j = 0; j < m; j++) {
			int v;
			std::cin >> v;
			if(v) {
				mat[i][j] = 1;
			}
		}
	}
	long long ans = 0;
	// count isolated
	for(int i = 0; i < n; i++) {
		long long cur = 0;
		for(int j = 0; j < m; j++) {
			if(mat[i][j]) cur = 1;
		}
		cur = cur * pot[m-1+n-1] % MOD;
		ans = (ans + cur) % MOD;
	}
	// uncount with previous
	Gauss tot;
	for(int i = 0; i < n; i++) {
		if(i) {
			mat[i-1][m] = 0;
			tot.add(mat[i-1]);
		}
		mat[i][m] = 1;
		int id = tot.add(mat[i]);
		if(id == m) {
			tot.eq[id].reset();
			tot.rank--;
			continue;
		}
		for(int j = i + 1; j < n; j++) {
			mat[j][m] = 1;
			auto b = tot.reduce(mat[j]);
			if(b[m] && b.count() == 1) {
				// contradiction
			} else {
				int rank = tot.rank;
				if(b.count()) rank++;
				//std::cout << "on pair " << i << ", " << j << " got rank " << rank << "\n";
				ans = (ans - pot[n-1+m-rank] + MOD) % MOD;
			}
		}
		if(id != -1) {
			tot.rank--;
			tot.eq[id].reset();
		}
	}
	std::cout << ans << '\n';
}

