int get(vector<int> v){
	for(int i = 0; i < v.size(); ++i) if(v[i] != 0) return i;
	return -1;
}

vector<int> er(vector<int> v, vector<int> e){
	int i1 = get(v);
	int i2 = get(e);
	if(i1 != i2) return v;
	for(int i = 0; i < v.size(); ++i){
		v[i] ^= e[i];
	}
	return v;
}

