  rep(k, M) {
    reps(i, k, N) {
      if (!mat[i][k]) continue;
      rep(j, M) {
        swap(mat[i][j], mat[k][j]);
      }

      rep(j, N) {
        if (k == j) continue;
        if (!mat[j][k]) continue;
        rep(l, M) {
          mat[j][l] += mat[k][l];
          mat[j][l] %= 2;
        }
      }
    }
  }

  int rank = 0;
  rep(k, min(M, N)) {
    if (mat[k][k]) rank++;
  }

  LL ans = mypow(2, N+M-1);
  ans += MOD - mypow(2, N+M-1-rank);
  ans %= MOD;
  printf("%lld\n", ans);
}

