const LLI mod = 998244353;
const double eps = 1e-9;

template <typename T> vector<vector<T>> transpose(vector<vector<T>> mat){
  int n = mat.size();
  int m = mat[0].size();

  vector<vector<T>> ret(m, vector<T>(n));

  REP(i,n) REP(j,m) ret[j][i] = mat[i][j];

  return ret;
}

template <typename T> int rank_matrix(vector<vector<T>> mat){

  int n = mat.size();
  int m = mat[0].size();
  int p = 0;

  if(n > m){
    auto t = transpose(mat);
    mat.swap(t);
    swap(n,m);
  }

  REP(i,n){
    if(p>=m) break;    

    bool b = true;
    FOR(j,i,n) if(abs(mat[j][p]) > eps) b = false;

    if(b){
      ++p;
      continue;
    }

    if(abs(mat[i][p]) <= eps ){
      FOR(j,i+1,n){
	if(abs(mat[j][p]) > eps){
	  swap(mat[i], mat[j]);
	  break;
	}
      }
    }

    FOR(j,i+1,n){
      double t = (double)mat[j][p] / mat[i][p];
      REP(k,m) mat[j][k] -= mat[i][k] * t;
    }
  }

  int r = 0;
  REP(i,n){
    REP(j,m){
      if(mat[i][j] != 0){
	++r;
	break;
      }
    }
  }

  return r;
}

