using namespace std;

const int md = 998244353;

inline void add(int &a, int b) {
  a += b;
  if (a >= md) a -= md;
}

inline void sub(int &a, int b) {
  a -= b;
  if (a < 0) a += md;
}

inline int mul(int a, int b) {
  return (int) ((long long) a * b % md);
}

inline int power(int a, long long b) {
  int res = 1;
  while (b > 0) {
    if (b & 1) {
      res = mul(res, a);
    }
    a = mul(a, a);
    b >>= 1;
  }
  return res;
}

inline int inv(int a) {
  a %= md;
  if (a < 0) a += md;
  int b = md, u = 0, v = 1;
  while (a) {
    int t = b / a;
    b -= t * a; swap(a, b);
    u -= t * v; swap(u, v);
  }
  assert(b == 1);
  if (u < 0) u += md;
  return u;
}

const int N = 330;

int main() {
  ios_base::sync_with_stdio(false);
  cin.tie(0);

  vector<int> fac(N), finv(N);
  fac[0] = finv[0] = 1;
  for (int i = 1; i < N; i++) {
    fac[i] = mul(fac[i - 1], i);
    finv[i] = inv(fac[i]);
  }

  auto comb = [&](int n, int m) {
    assert(n >= m);
    int res = fac[n];
    res = mul(res, finv[m]);
    res = mul(res, finv[n - m]);
    return res;
  };

  int n, m;
  cin >> n >> m;
  vector< vector<int> > a(n, vector<int>(m));
  for (int i = 0; i < n; i++) {
    for (int j = 0; j < m; j++) {
      cin >> a[i][j];
    }
  }

  auto lowbit = [](vector<int> a) {
    for (int i = 0; i < (int) a.size(); i++) {
      if (a[i] == 1) {
        return i;
      }
    }
    return -1;
  };

  auto Xor = [&](int i, int j) {
    for (int k = 0; k < m; k++) {
      a[i][k] ^= a[j][k];
    }
  };

  vector<int> base;
  for (int i = 0; i < n; i++) {
    for (auto j : base) {
      if (a[i][ lowbit(a[j]) ] == 1) {
        Xor(i, j);
      }
    }
    if (lowbit(a[i]) != -1) {
      base.push_back(i);
    }
  }

  int r = base.size();
  // cerr << r << '\n';
  int res = 0;
  for (int i = 1; i <= r; i += 2) {
    add(res, mul(comb(r, i), power(3, r - i)));
  }
  res = mul(res, power(2, (n - r) + (m - r)));
  cout << res << '\n';
  return 0;
}

