using Int = int;

template<Int MOD>
struct ModInt {
    constexpr static Int mod = MOD;
    Int v;

    ModInt(long long _v = 0) : v(set(_v)) {}
    ModInt(const ModInt &r) : v(set(r.v)) {}

    inline static Int set(const Int x) { return x < 0 ? (x % mod) + mod : x % mod; }
    inline void set() { v = set(v); }

    bool operator<(ModInt r) const { return v < r.v; }
    bool operator>(ModInt r) const { return r.v < v; }
    bool operator==(ModInt r) const { return v == r.v; }
    bool operator!= (ModInt r) const { return v != r.v; }

    ModInt operator-() const { return ModInt(v ? mod - v : v); }
    ModInt &operator=(const ModInt &r) { if (this != &r) v = set(r.v); return *this; }
    ModInt &operator+=(ModInt r) { (v += r.v) %= mod; return *this; }
    ModInt &operator-=(ModInt r) { (v -= r.v - mod) %= mod; return *this; }
    // ModInt &operator*=(ModInt r) { v = (__uint128_t(v) * r.v) % mod; return *this; }
    ModInt &operator*=(ModInt r) { v = 1ULL * v * r.v % mod; return *this; }
    ModInt &operator/=(ModInt r) { *this *= r.inv(); return *this; }
    ModInt operator+(ModInt r) const { return ModInt(*this) += r; }
    ModInt operator-(ModInt r) const { return ModInt(*this) -= r; }
    ModInt operator*(ModInt r) const { return ModInt(*this) *= r; }
    ModInt operator/(ModInt r) const { return ModInt(*this) /= r; }

    ModInt inv() const {
        long long a = v, b = mod, u = 1, w = 0;
        while (b) {
            long long t = a / b;
            std::swap(a -= t * b, b);
            std::swap(u -= t * w, w);
        }
        return ModInt(u);
    }

    ModInt pow(Int e) {
        ModInt a = *this, x(1);
        for ( ; 0 < e; e >>= 1) { if (e & 1) x *= a; a *= a; }
        return x;
    }
    inline ModInt pow(ModInt &e) { return pow(e.v); }
};

std::ostream &operator<<(std::ostream &os, const auto &r) { return os << r.v; }
std::istream &operator>>(std::istream &is, auto &r) { is >> r.v; r.set();return is; }

using Mod2 = ModInt<2>;
using Type = ModInt<998244353>;
using Vector = vector<Mod2>;
using Matrix = vector<Vector>;

int Rank(Matrix &A) {
    const int N = A.size(), M = A[0].size();
    int rank = 0;
    vector<bool> used(N, false);

    for (int c = 0; c < M; ++c) {
        int pivot = 0;
        while (pivot < N && (used[pivot] || A[pivot][c] == 0)) ++pivot;
        if (pivot == N) continue;

        ++rank;
        Mod2 v = A[pivot][c];
        for (int j = 0; j < N; ++j) A[pivot][j] /= v;
        for (int r = 0; r < N; ++r) {
            if (r == pivot) continue;
            for (int j = c + 1; j < M; ++j)
                A[r][j] -= A[r][c] * A[pivot][j];
        }
    }

    return rank;
}

int main() {
    cin.tie(0); ios::sync_with_stdio(false);

    int n, m;
    cin >> n >> m;
    Matrix A(n, Vector(m));
    for (int i = 0; i < n; ++i)
        for (int j = 0; j < m; ++j)
            cin >> A[i][j];

    int rank = Rank(A);
    Type res = Type(2).pow(m + n - 1) - Type(2).pow(m + n - rank - 1);
    cout << res << endl;

    return 0;
}

