void Gauss(vector<vector<double>> &mat) {

    int n = (int)mat.size();

    for(int i = 0; i < n; i++) {
        bool ok = false;
        for(int j = i; j < n; j++) {
            if(fabs(mat[j][i]) > __gauss_template_EPS) {
                swap(mat[j], mat[i]);
                ok = true;
                break;
            }
        }

        if(!ok) continue;

        double fs = mat[i][i];

        for(int j = i+1; j < n; j++) {
            double r = mat[j][i] / fs;

            for(int k = i; k < n; k++) {

                mat[j][k] -= mat[i][k] * r;
            }
        }
    }
}



