long long exp(int a, int b) {
    if(b == 0) return 1;
    if(b == 1) return a;
    long long tmp = exp(a, b/2);
    if(b & 1) {
        return (long long)tmp*tmp%__exp_template_MOD*a%__exp_template_MOD;
    }
    return (long long)tmp*tmp%__exp_template_MOD;
}

