void add(bitset<N> x) {
    for (int i = m; i >= 1; --i) {
        if (x[i] == 0) continue;
        if (x[i] && b[i] != 0) {
            x ^= b[i];
        } else {
            b[i] = x; basis.emplace_back(x); break;
        }
    }
    if (x.count() == 0) return;
}

