int mod = 998244353;
int fact[301];
int finv[301];

int comb(int n, int k) {
	return fact[n] * finv[k] % mod * finv[n - k] % mod;
}

void henkei(Mat &a) {
	int h = a.size();
	int w = a[0].size();

	int i, j, k;
	int cor = 0;

	rep(i, w) {
		for (j = cor; j < h; j++) {
			if (a[j][i] == 1) {
				break;
			}
		}
		if (j == h) continue;

		int pos = j;
		rep(j, w) {
			swap(a[cor][j], a[pos][j]);
		}

		for (j = cor + 1; j < h; j++) {
			if (a[j][i] == 0) continue;
			rep(k, w) {
				a[j][k] ^= a[cor][k];
			}
		}
		cor++;
	}
}

void tenti(Mat &a) {
	int h = a.size();
	int w = a[0].size();
	Mat b(w, Array(h));

	int i, j;
	rep(i, h) rep(j, w) b[j][i] = a[i][j];
	a = b;
}

int _rank(Mat &a) {
	int i;
	rep(i, a.size()) {
		if (i >= a[0].size() || a[i][i] == 0) break;
	}
	return i;
}

