const int Maxn = 305;
const int mod = 998244353;

int pw2[Maxn];
int n, m;
int M[Maxn][Maxn];

void Swap(int r1, int r2)
{
    for (int j = 0; j < m; j++)
        swap(M[r1][j], M[r2][j]);
}

void Add(int r1, int r2)
{
    for (int j = 0; j < m; j++)
        M[r2][j] ^= M[r1][j];
}

int main()
{
    pw2[0] = 1;
    for (int i = 1; i < Maxn; i++)
        pw2[i] = 2ll * pw2[i - 1] % mod;
    scanf("%d %d", &n, &m);
    for (int i = 0; i < n; i++)
        for (int j = 0; j < m; j++)
            scanf("%d", &M[i][j]);
    int r = 0;
    for (int j = 0; j < m; j++) {
        int i = r;
        while (i < n && M[i][j] == 0) i++;
        if (i == n) continue;
        Swap(i, r);
        for (int l = r + 1; l < n; l++) if (M[l][j])
            Add(r, l);
        r++;
    }
    int res = (pw2[n] - pw2[n - r] + mod) % mod;
    res = ll(res) * pw2[m - 1] % mod;
    printf("%d\n", res);
    return 0;
}

