const int MAXcomb = 200010;
ll fac[MAXcomb], finv[MAXcomb], inv[MAXcomb];
//facn!,finv1/n!
//inv
void COMinit(){
    fac[0] = fac[1] = 1;
    finv[0] = finv[1] = 1;
    inv[1] = 1;
    for(int i = 2; i < MAXcomb; i++){
        fac[i] = fac[i-1] * i % MOD;
        inv[i] = MOD - inv[MOD%i] * (MOD/i) % MOD;
        finv[i] = finv[i-1] * inv[i] % MOD;
    }
}
ll comb(int n, int k){
    if(n < k) return 0;
    if(n < 0 || k < 0) return 0;
    return fac[n] * finv[k] % MOD * finv[n-k] % MOD;
}

const int MAXkai = 200010;
ll kai_memo[MAXkai];
ll kai(ll N){
  if(kai_memo[N] != 0) return kai_memo[N];
  if(N <= 1) return 1;
  return kai_memo[N] = N * kai(N-1) % MOD;
}


ll Rank(ll N, ll M, vector<vector<ll>> A){
  ll next = 0;
  rep(i, M){
    for(ll j = next; j < N; j++){
      if(A[j][i]){
        swap(A[next], A[j]);
      }
    }
    if(!A[next][i]) continue;
    rep(j, N)if(j != next){
      if(A[j][i]){
        rep(k, M) A[j][k] ^= A[next][k];
      }
    }
    next++;
  }
  return next;
}

ll N, M;
