void solve(){
  COMinit();
  cin >> N >> M;
  A.resize(N);
  rep(i, N) A[i].resize(M);
  rep(i, N)rep(j, M) cin >> A[i][j];
  ll r = Rank(N, M, A);
  ll ans = 0;
  rep(i, r+1){
    ll add = comb(r, i) * pow_mod(2, r-i) % MOD;
    for(ll j = 1; j <= i; j += 2){
      ans += add * comb(i, j) % MOD;
      ans %= MOD;
    }
  }
  ans *= pow_mod(2, N + M - 2 * r);
  ans %= MOD;
  cout << ans << endl;
}


