constexpr ll mod = 998244353;
constexpr ll modsize = 0;
vector<ll> fac(modsize);
vector<ll> inv(modsize);
vector<ll> facinv(modsize);

void modcalc() {
	if (modsize == 0) abort();
	fac[0] = 1; fac[1] = 1; inv[1] = 1;
	facinv[0] = 1; facinv[1] = 1;
	for (ll i = 2; i < modsize; i++) {
		fac[i] = fac[i - 1] * i % mod;
		inv[i] = mod - inv[mod % i] * (mod / i) % mod;
		facinv[i] = facinv[i - 1] * inv[i] % mod;
	}
}

ll modinv(ll a) {
	if (a == 0) abort();
	ll b = mod, u = 1, v = 0;
	while (b) {
		ll t = a / b;
		a -= t * b; swap(a, b);
		u -= t * v; swap(u, v);
	}
	u %= mod;
	if (u < 0) u += mod;
	return u;
}

ll modpow(ll a, ll b) {
	ll ans = 1;
	a %= mod;
	while (b) {
		if (b & 1) ans = ans * a % mod;
		a = a * a % mod;
		b >>= 1;
	}
	return ans;
}

ll modcomb(ll n, ll k) {
	if (n < 0 || k < 0 || n < k) return 0;
	return fac[n] * facinv[k] % mod * facinv[n - k] % mod;
}

ll modperm(ll n, ll k) {
	if (n < 0 || k < 0 || n < k) return 0;
	return fac[n] * facinv[n - k] % mod;
}

ll modhom(ll n, ll k) {
	if (n < 0 || k < 0 || n == 0 && k > 0) return 0;
	if (n == 0 && k == 0) return 1;
	return fac[n + k - 1] * facinv[k] % mod * facinv[n - 1] % mod;
}

int mat_rank(vector<vector<int>> mat) {
	int n = mat.size();
	int m = mat[0].size();
	int st = 0;
	rep(j, m) {
		int idx = -1;
		for (int i = st; i < n; i++) {
			if (mat[i][j]) {
				if (idx == -1) {
					idx = i;
					continue;
				}
				rep(k, m) {
					mat[i][k] ^= mat[idx][k];
				}
			}
		}
		for (int i = st - 1; i >= 0; i--) {
			if (mat[i][j]) {
				if (idx == -1) continue;
				rep(k, m) {
					mat[i][k] ^= mat[idx][k];
				}
			}
		}
		if (idx != -1) {
			rep(k, m) {
				swap(mat[idx][k], mat[st][k]);
			}
			st++;
		}
	}
	return st;
}

