template<typename T>
T binpow(T n, T s)
{
    if (s <= 0)
        return 1LL;
    if (s % 2 == 0){
        T b = binpow(n, s / 2);
        return ( 1LL * b * b ) % base2;
    } else {
        return (1LL* binpow(n, s - 1) * n) % base2;
    }
}

