int gauss (vector < bitset<N> > a, int n, int m) {    
    int rank = m;
    vector<char> line_used (n);
    for (int i=0; i<m; ++i) {
        int j;
        for (j=0; j<n; ++j)
            if (!line_used[j] && a[j][i])
                break;
        if (j == n)
            --rank;
        else {
            line_used[j] = true;
            for (int k=0; k<n; ++k)
                if (k != j && a[k][i])
                    a[k] ^= a[j];
        }
    }
    return rank;
}

