ll power_mod(ll num, ll power) {
    ll prod = 1;
    num %= mod;
    while (power > 0) {
        if (power & 1) {
            prod = prod * num % mod;
        }
        num = num * num % mod;
        power >>= 1;
    }
    return prod;
}

