typedef pair<ll, int> pli;
typedef pair<ll, ll> pll;

typedef int Number;
typedef vector<Number> Array;
typedef vector<Array> Matrix;
const Number eps = 0;

int mat_rank(Matrix A) {
    const int n = A.size(), m = A[0].size();
    int r = 0;
    for (int i = 0; r < n && i < m; ++i) {
        int pivot = r;
        for (int j = r + 1; j < n; ++j) {
            if (fabs(A[j][i]) > fabs(A[pivot][i])) {
                pivot = j;
            }
        }
        swap(A[pivot], A[r]);
        if (fabs(A[r][i]) <= eps) {
            continue;
        }
        for (int k = m - 1; k >= i; --k) {
            A[r][k] /= A[r][i];
        }
        for (int j = r + 1; j < n; ++j) {
            for (int k = i; k < m; ++k) {
                A[j][k] -= A[r][k] * A[j][i];
            }
        }
        ++r;
    }
    return r;
}

const ll MOD = 998244353;

int N, M;
Matrix a;

ll pow2(int n) {
    ll ret = 1;
    for (int i = 0; i < n; i++) {
        ret = (ret << 1) % MOD;
    }
    return ret;
}

ll solve() {
    int r = mat_rank(a);
    return (pow2(N + M - 1) + MOD - pow2(N + M - r - 1)) % MOD;
}

