		int h = mat.size(), w = mat[0].size();
		int res = 0, now = 0;
		for (int i = 0; i < h; i++) {
			T ma = 0.0;
			int pivot;
			for (int j = i; j < h; j++) {
				if (mat[j][now] > ma) {
					ma = mat[j][now];
					pivot = j;
