			for (int j = 0; j < n; j++) {
				mat[i][j] *= tmp;
				inv.mat[i][j] *= tmp;
			}
			for (int j = 0; j < n; j++) {
				if (i != j) {
					T tmp2 = mat[j][i];
					for (int k = 0; k < n; k++) {
						mat[j][k] -= mat[i][k] * tmp2;
						inv.mat[j][k] -= inv.mat[i][k] * tmp2;
					}
				}
			}
		}
