  Solver2(LL n, LL m) : n(n), m(m), a(n, vector<int>(m)){};

  int sweep(vector<vector<int>> X) {
    int rank = 0;
    for (int col = 0; col < m; ++col) {
      int nonzeroRow = -1;
      for (int r = rank; r < n; ++r) {
        if (X[r][col]) {
          nonzeroRow = r;
          break;
        }
      }
      if (nonzeroRow == -1) continue;
      swap(a[rank], a[nonzeroRow]);
      for (int r = rank + 1; r < n; ++r) {
        if (X[r][col]) {
          for (int c = col; c < m; ++c) {
            X[r][c] ^= X[rank][c];
          }
        }
      }
      rank++;
    }
    return rank;
  }
  void solve() {
    for (int i = 0; i < n; ++i) {
      for (int j = 0; j < m; ++j) {
        cin >> a[i][j];
      }
