int compute_rank(vector< vector< ll > > A) {
    int n = A.size();
    int m = A[0].size();
    // cout << n << " " << m << endl;
    int rank = n;
    vector<bool> row_selected(n, false);
    for (int i = 0; i < m; ++i) {
        int j;
        for (j = 0; j < n; ++j) {
            if (!row_selected[j] && A[j][i])
                break;
        }

        if(j != n){
            row_selected[j] = true;
            for (int p = i + 1; p < m; ++p)
                A[j][p] /= A[j][i];

            A[j][i] = 1;

            for (int k = 0; k < n; ++k) {
                if (k != j && A[k][i]) {
                    for (int p = i + 1; p < m; ++p)
                        A[k][p] -= A[j][p] * A[k][i];

                    A[k][i] = 0;
                }
            }
        }
    }

    for(int i = 0 ; i < n ; i++){
        bool has_one = false;

        for(int j = 0 ; j < m ; j++){
            if(A[i][j]){
                has_one = true;
                break;
            }
        }

        if(!has_one){
            rank--;
        }
    }

    return rank;
}

vector< vector< ll > > a;

ll fast_pow(ll base, int n){
    ll carry = base;
    ll res = 1LL;

    while(n){
        if(n & 1){
            res = (res * carry) % mod;
        }

        n /= 2;
        carry = (carry * carry) % mod;
    }

    return res;
}

ll MOD(ll x){
    return ((x % mod) + mod) % mod;
}

