const int MOD = 998244353;
const int kN = 300 + 5;
int n, m;
int a[kN][kN];

int power(int a, int b) {
  int ret = 1;
  while (b) {
    if (b & 1) ret = ret * 1LL * a % MOD;
    a = a * 1LL * a % MOD;
    b >>= 1;
  }
  return ret;
}

int gauss() {
  int ret = 0;
  for (int i = 0, p = 0; i < n; ++ i) {
    int k = -1;
    while (k == -1 && p != n) {
      for (int j = i; j < n; ++ j) {
        if (a[j][p]) {
          k = j;
          break;
        }
      }
      if (k == -1) {
        ++ p;
      }
    }
    if (k == -1) break;
    ret ++;
    for (int r = 0; r < n; ++ r)
      std::swap(a[i][r], a[k][r]);
    for (int w = i + 1; w < n; ++ w) {
      if (a[w][p]) {
        for (int r = 0; r < n; ++ r)
          a[w][r] ^= a[i][r];
      }
    }
    ++ p;
  }
  return ret;
}

int main() {
  scanf("%d%d", &n, &m);
  for (int i = 0; i < n; ++ i) {
    for (int j = 0; j < m; ++ j) {
      scanf("%d", &a[i][j]);
    }
  }
  int rank = gauss();
  int result = (power(2, n) - power(2, n - rank) + MOD) % MOD * 1LL * power(2, m - 1) % MOD;
  printf("%d\n", result);
}

