const int MAXkai = 200010;
ll kai_memo[MAXkai];
ll kai(ll N){
  if(kai_memo[N] != 0) return kai_memo[N];
  if(N <= 1) return 1;
  return kai_memo[N] = N * kai(N-1) % MOD;
}


ll Rank(ll N, ll M, vector<vector<ll>> A){
  ll next = 0;
  rep(i, M){
    for(ll j = next; j < N; j++){
      if(A[j][i]){
        swap(A[next], A[j]);
      }
    }
    if(!A[next][i]) continue;
    rep(j, N)if(j != next){
      if(A[j][i]){
        rep(k, M) A[j][k] ^= A[next][k];
      }
    }
    next++;
    if(next == N) break;
  }
  return next;
}

ll N, M;
vector<vector<ll>> A;


void solve(){
  COMinit();
  cin >> N >> M;
  A.resize(N);
  rep(i, N) A[i].resize(M);
  rep(i, N)rep(j, M) cin >> A[i][j];
  ll r = Rank(N, M, A);
  ll ans = 0;
  rep(i, r+1){
    ll add = comb(r, i) * pow_mod(2, r-i) % MOD;
    for(ll j = 1; j <= i; j += 2){
      ans += add * comb(i, j) % MOD;
      ans %= MOD;
    }
  }
  ans *= pow_mod(2, N + M - 2 * r);
  ans %= MOD;
  cout << ans << endl;
}


