		REP(j, m) if (a[i][j]) f = true;
		if (f) ++one;
	}

	int ans = powMod(2, n, MOD) - powMod(2, n - one, MOD);
	(ans *= powMod(2, m - 1, MOD)) %= MOD;
	if (ans < 0) ans += MOD;
	OUT(ans)BR;

	return 0;
}

