void kill() {
  int rank = 0;
  for (int i = 0; i < m; ++i) {
    int x = -1;
    for (int j = rank; j < n; ++j)
      if (a[j][i])
        x = j;
    if (x != -1) {
      swp(x, rank);
      ++rank;
      for (int j = rank; j < n; ++j)
        if (a[j][i])
          sbt(j, rank - 1);
    }
  }

  int ans = sub(bin(2, n), bin(2, n - rank));
  ans = mul(ans, bin(2, m - 1));

  cout << ans << "\n";
}

