const int MOD = 998244353;

int main() {
	int n, m; reader(n, m);
	using bs = bitset<300>;
	vector<bs> v(n);
	FOR(i, n) {
		FOR(j, m) {
			int x; reader(x);
			if (x == 1) v[i].set(j);
		}
	}

	int freeColRows = 0;
	{
		int removed = 0;
		FOR(j, m) {
			int found = -1;
			for (int i = removed; i < n; i++) {
				if (v[i][j]) {
					found = i;
					break;
				}
			}
			if (found == -1) continue;
			swap(v[removed], v[found]);
			FOR(i, n) {
				if (i != removed && v[i][j]) {
					v[i] ^= v[removed];
				}
			}
			removed++;
		}

		freeColRows = m - removed;
	}
	vector<Pll> vp;
	FOR(i, n) {
		int cnt = 0;
		FOR(j, m) if (v[i][j])cnt++;
		if (cnt == 0) {
			freeColRows++;
			continue;
		}

		ll one = mod_pow(2, cnt - 1, MOD);
		ll zero = one * 3 % MOD;
		vp.emplace_back(zero, one);
	}

	Pll ans = { 1,0 };
	for (auto& kv : vp) {
		Pll nxt;
		nxt.first = (ans.first * kv.first + ans.second * kv.second) % MOD;
		nxt.second = (ans.first * kv.second + ans.second * kv.first) % MOD;
		ans = nxt;
	}
	ll ml = mod_pow(2, freeColRows, MOD);

	writerLn(ans.second * ml % MOD);

	return 0;
}

