        a[i][m] = 0;
    }
    int l = 0, c = 0, var_free = 0;
    while(l < n && c < m) {
        int poz = -1;
        for(int i = l; i < n && poz == -1; i++)
            if(a[i][c] == 1)
                poz = i;
        if(poz == -1) {
            // n-am putut gasi, e deja 0
            c++;
            var_free++; // este variabila libea
            continue;
        }
        swap(a[l], a[poz]);
        for(int i = l + 1; i < n; ++i)
            if(a[i][c] == 1)
                a[i] ^= a[l];
        l++;
        c++;
    }

    bool zero_sub = 1;

    for(int i = 0; i < n; ++i) {
        if(a[i][m] == 1) {
            bool any = 1;
            for(int j = 0; j < m && !any; j++)
                any |= a[i][j];
