        for (int j = src.size() - 1; j > i; j--) {
            int x = src[j]._Find_first(), y = src[j - 1]._Find_first();
            if (x < y) { src[j - 1] ^= src[j]; }
            if (x <= y) { src[j] ^= src[j - 1]; }
        }
        if (src[i].none()) { break; }
        rank++;
    }
    constexpr int MOD = 998244353;
    auto mpow = [&](int n) {
        long long ret = 1, mul = 2;
        while (n) {
            if (n & 1) { (ret *= mul) %= MOD; }
            (mul *= mul) %= MOD;
            n >>= 1;
        }
        return ret;
    };
    io.putint((mpow(N + M - 1) - mpow(N + M - rank - 1) + MOD) % MOD);
}

