int gauss(vector<vector<int>> A) {
    int n = A.size(), m = A[0].size();
    int r = 0;
    for (int i = 0; r < n && i < m; ++i) {
        int piv = r;
        rep(j, r + 1, n) if (abs(A[j][i]) > abs(A[piv][i])) piv = j;
        A[r].swap(A[piv]);
        if (abs(A[r][i]) == 0) continue;
        rrep(k, m - 1, i) A[r][k] /= A[r][i];
        rep(j, r + 1, n) rrep(k, m - 1, i) A[j][k] ^= A[r][k] * A[j][i];
        ++r;
    }
    return r;
}
