void swap_row(int x, int y) {
	static int t[N];
	memcpy(t, a[x], sizeof(int) * m);
	memcpy(a[x], a[y], sizeof(int) * m);
	memcpy(a[y], t, sizeof(int) * m);
}

void xor_row(int x, int y) {
	range(i, 0, m - 1) a[x][i] ^= a[y][i];
}

// return rank
int gaussian(int x, int y) {
	if (x >= n) return n;
	if (y >= m) {
		return x;
	}
	int p = n;
	range(i, x, n - 1) if (a[i][y] == 1) { p = i; break; }
	if (p >= n) {
		return gaussian(x, y + 1);
	} else {
		if (x != p) swap_row(x, p);
		range(i, x + 1, n - 1) {
			if (a[i][y] == 0) continue;
			xor_row(i, x);
		}
		return gaussian(x + 1, y + 1);
	}
}

i64 qpow(i64 a, i64 b) {
	if (b == 0) return 1;
	i64 t = qpow(a, b >> 1ll);
	t = t * t % modp;
	if (b & 1) t = t * a % modp;
	return t;
}

