int findRank(int n,int m)
{
	for(int i=0;i<m;i++)
	{
		int id = i;
		for(int j=i;j<n;j++)
			if(a[j][i] == 1)
				id = j;
		for(int j=0;j<m;j++)
			swap(a[i][j], a[id][j]);
		for(int j=0;j<n;j++)
		{
			if(a[j][i]==0 || i == j)
				continue;
			for(int k=0;k<m;k++)
				a[j][k] ^= a[i][k];
		}
	}
	int rank = 0;
	for(int i=0;i<n;i++)
	{
		bool flag = 0;
		for(int j=0;j<m;j++)
			if(a[i][j])
				flag = 1;
		if(flag)
			rank++;
	}
	return rank;
}
