typedef pair <int,int> P;

struct Rank
{
	struct P
	{
		vector <int> vec;
		int val;
		P(){}
		P(vector <int> vec,int val=0):vec(vec),val(val){}
	};
	vector <P> express[SIZE];
	bool use[SIZE];

	void add(vector <int> vec,int n,int v)
	{
		memset(use,false,sizeof(use));
		for(int i=0;i<vec.size();i++)
		{
			use[vec[i]]=true;
		}
		vector <int> ad;
		int c=-1;
		for(int i=0;i<n;i++)
		{
			if(use[i])
			{
				if(c==-1) c=i;
				ad.push_back(1);
			}
			else ad.push_back(0);
		}
		if(c!=-1) express[c].push_back(P(ad,v));
	}
	ll calc(int n)
	{
		ll ret=1;
		for(int i=0;i<n;i++)
		{
			if(express[i].empty())
			{
				ret*=2;
				if(ret>=MOD) ret-=MOD;
			}
			else
			{
				P p=express[i][0];
				for(int j=1;j<express[i].size();j++)
				{
					P q=express[i][j];
					if(q.vec==p.vec)
					{
						if(p.val!=q.val)
						{
							return 0;
						}
						continue;
					}
					int c=-1;
					for(int j=i;j<n;j++)
					{
						q.vec[j]^=p.vec[j];
						if(c==-1&&q.vec[j]==1)
						{
							c=j;
						}
					}
					q.val^=p.val;
					express[c].push_back(q);
				}
			}
		}
		return ret;
	}
}rnk;
int A[SIZE][SIZE];
ll rt[SIZE*2];

void make()
{
	rt[0]=1;
	for(int i=1;i<SIZE*2;i++) rt[i]=rt[i-1]*2LL%MOD;
}
int main()
{
	make();
	int n,m;
	scanf("%d %d",&n,&m);
	for(int i=0;i<n;i++)
	{
		vector <int> vec;
		for(int j=0;j<m;j++)
		{
			scanf("%d",&A[i][j]);
			if(A[i][j]==1) vec.push_back(j);
		}
		rnk.add(vec,m,0);
	}
	ll rk=rnk.calc(m);
	ll all=rt[n+m];
	all-=rk*rt[n]%MOD;
	if(all<0) all+=MOD;
	if(all%2==1) all+=MOD;
	all/=2;
	printf("%lld\n",all);
	return 0;
}

