//{{{ gauss
template<class T>
int gauss(MATRIX<T> &A, VECTOR<T> &b) {
	//int gauss(& A) {//returns rank
	const int n = A.size(),m = A[0].size();
	int pi = 0;
	for(int pj = 0;pj < m;pj++){
		for(int i = pi+1; i < n; i++) {
			if (abs(A[i][pj]) > abs(A[pi][pj])) {
				swap(A[i], A[pi]);
				swap(b[i], b[pi]);
			}
		}
		if (abs(A[pi][pj]) > 0) {
			T d = A[pi][pj];//1/A[pi][pj]
			REP(j, m)A[pi][j] *= d;
			b[pi] *= d;
			REP(i,n){
				if(i==pi)continue;
				T k = A[i][pj];
				REP(j, m) A[i][j] -= k * A[pi][j];
				b[i] -= k * b[pi];
			}
			pi++;
		}
	}
	return pi;
	/*
	   for(int i = pi; i < n; i++)
	   if (abs(b[i]) > 0)
	   throw Inconsistent();
	   if (pi < m || pj < m)
	   throw Ambiguous();
	   for(int j = m-1; j >= 0; j--)
	   REP(i, j)
	   b[i] = modulo(b[i] - b[j] * A[i][j]);
	   */
}
