inline void insert(bitset<300> x)
{
	DREP(i,m,1)if(x[i])
	{
		if(!vis[i]){ s[i]=x;vis[i]=1; break;}
		else x^=s[i];
	}
}

inline void init()
{
	n=read();m=read();
	REP(i,1,n)REP(j,1,m)a[i][j]=read();
	REP(j,1,m)
	{
